import React,{useContext,useState,useMemo} from 'react'
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'
import { isRouteErrorResponse, Link } from 'react-router-dom'
import { AppState,DepositState } from '../App'
import './deposit.css'
import { useForm } from 'react-hook-form'
import Modal from './Modal'
import DepositModal from './DepositModal'

const schema = yup.object().shape({
    deposit:yup.number().required().positive()
})
const Deposit = () => {
  const {register,handleSubmit,formState:{errors}} = useForm({
      resolver:yupResolver(schema)
  })
    const {userInfo} = useContext(AppState)
    const {amountDeposit,setDeposit} = useContext(DepositState)

    const [modal,setModal] = useState(false)
    const [showDepositModal,setDepositModal] = useState(false)

    const  OnDeposit=(data,event)=>{
      event.preventDefault()
          if(data<=0)
            {
              setModal(false)
              return
            }     

          setDeposit(data)


    }

     if(showDepositModal)
     {
        setDepositModal(false)
     }

  return (
  <>

    <div className='deposit'>
    {!showDepositModal || <Modal openmodal={modal} setModal={setModal}   /> && !showDepositModal && <DepositModal/> || showDepositModal && <Modal/> }


       <h2>Welcome {userInfo.Fname}</h2>
      <div className='main'>
         <form className='deposit__form' onSubmit={handleSubmit(OnDeposit)}>
            <label className='deposit__label'>How much do you want do deposit</label>
            <input type='text' name='deposit' className='deposit__input'
            {...register('deposit')}
            />
            <p className='deposit__error'>{errors.deposit && 'You cannot deposit a null value'}</p>
            <div>
                <button className='deposit__button' onClick={()=>{  

                   { !errors.deposit &&  setModal(true)}
                }} >Deposit</button>

            </div>
         </form>


      </div>


      <Link to='/'>home</Link>
    </div>
    </>
  )
}

export default Deposit
