import React from 'react'
import {NavLink} from 'react-router-dom'
import './App.css'
const Nav = () => {
  return (
    <div id='nav'>
        <NavLink className='nav-link' to='/'

          style={({isActive})=>{
            return {
                color: isActive?   'red' : 'grey'
            }

          }}

        >Home</NavLink>
        <NavLink className='nav-link' to='/products'>Products</NavLink>

    </div>
  )
}

export default Nav
