    const {amountDeposit} = useContext(DepositState)  
    const {userInfo} = useContext(AppState)



  return (
    <div className='depositModal'>

         {!showDeposit &&  <h2>{userInfo.Fname}You have successfully deposited {amountDeposit.deposit}</h2>} 
    </div>
  )
}

