import React,{useContext,useState} from 'react'
import { DepositState,AppState } from '../App'
import DepositModal from './DepositModal'
import './modal.css'
import Login from './Login'
const Modal = ({openmodal,setModal}) => {
    const {amountDeposit,setDeposit} = useContext(DepositState)
    const {userInfo} = useContext(AppState)

  const [showDeposit,setShowDeposit]  = useState(false)

    if(!openmodal)
    {
       return null
    }

    const handleSubmit=()=>{
      setShowDeposit(true)

    }

  return (
   <div>
       { !showDeposit && <DepositModal/> && showDeposit && <DepositModal/>}
    <div className='modal'>

        <form onSubmit={handleSubmit()}>
         <h2 className='title'>Do you want to deposit {amountDeposit.deposit}</h2>
         <button className='deposit__button1' onClick={()=>{
            setModal(false)
         }}>Cancel</button>
        <button className='deposit__button2' onClick={()=>{
              setModal(false)

         }}>Deposit</button> 
        </form>

    </div>
    </div> 
  )
}

export default Modal
