import React,{useContext,useState,useMemo} from 'react'
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'
import { Link } from 'react-router-dom'
import { AppState } from '../App'
import './deposit.css'
import { useForm } from 'react-hook-form'
import Modal from './Modal'


const schema = yup.object().shape({
    deposit:yup.number()
})
const Deposit = () => {
  const {register,handleSubmit,formState:{errors}} = useForm({
      resolver:yupResolver(schema)
  })
    const {userInfo} = useContext(AppState)
    const [deposit,setDeposit] = useState()

    const [modal,openModal] = useState(false)


    const  OnDeposit=(data)=>{
          setDeposit(data)


    }

  return (
    <div className='deposit'>
       <h2>Welcome {userInfo.Fname}</h2>
      <div className='main'>
         <form className='deposit__form'>
            <label className='deposit__label'>How much do you want do deposit</label>
            <input type='text' name='deposit' className='deposit__input'
            {...register('deposit')}
            />
            <div>
                <button className='deposit__button' onClick={handleSubmit()}>Deposit</button>
                <Modal/>
            </div>
         </form>
      </div>

      <Link to='/'>home</Link>
    </div>
  )
}

export default Deposit
