import './modal.css'
import Login from './Login'
const Modall = ({openmodal,setModal}) => {
    const {amountDeposit,setDeposit} = useContext(DepositState)
    const {userInfo} = useContext(AppState)


    const [show,setShow] = useState(false)
  const [showDeposit,setShowDeposit]  = useState(false)

    if(!openmodal)
    {
       return null
    }




  return (
   <div>
         { show &&   <DepositModal/>}

    <div className='modal'>

         <h2 className='title'>Do you want to deposit {amountDeposit.deposit}</h2>
         <button className='deposit__button1' onClick={()=>{
            setModal(false)
         }}>Cancel</button>
         <button className='deposit__button2'
            onClick={()=>{
                 setShow(true)
            }}
         >Deposit</button>

    </div>


    </div> 
  )
}

export default Modall
