import React,{useContext,useState} from 'react'
import { DepositState,AppState } from '../App'
import './modal.css'
import DepositModal from './DepositModal'
import Login from './Login'
const Modal = ({openmodal,setModal}) => {
    const {amountDeposit,setDeposit} = useContext(DepositState)
    const {userInfo} = useContext(AppState)
    const [showdepositModal,setShowDeositModal] = useState(false)
    if(!openmodal)
    {
       return null
    }
  return (
    <div className='modal'>
         { showdepositModal && <DepositModal/> }
         <h2 className='title'>Do you want to deposit {amountDeposit.deposit}</h2>
         <button className='deposit__button1' onClick={()=>{
            setModal(false)
         }}>Cancel</button>
         <button className='deposit__button2' onClick={()=>{
              setModal(false)
              setShowDeositModal(true)

         }}>Deposit</button>


    </div>
  )
}

export default Modal
