import './Withdraw.css'
import { useForm, UseForm } from 'react-hook-form'
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'
import { withDrawState,AppState,DepositState } from '../App' 


const schema = yup.object().shape({
  withdraw:yup.number().required().positive()

})

const Withdraw = () => {
   const {amountWithdraw,setWithdraw} = useContext(withDrawState)
   const {amountDeposit} = useContext(DepositState)
   const {userInfo} = useContext(AppState)


   const {register,handleSubmit,formState:{errors}} = useForm({

      resolver:yupResolver(schema)
   })

  return (
    <div>
        <div className='withdraw'>
             <h2> Welcome {userInfo.Fname} </h2>

             <div className='withdaw__contents'>
                  <form>
                    <label className='withdraw__label'>Enter Amount</label>
                    <input placeholder='Amount' name='withdraw' className='withdraw__input'/>

                  </form>


             </div>
        </div>

    </div>
  )
}

export default Withdraw
